function largestOfFour(arr) {
  // You can do this!
  let ansArr = [];
  for(let i = 0; i < arr.length; i++) {
    var ans = arr[i][0];
    for (let j = 0; j < arr[i].length; j++) {
      if (arr[i][j] > ans) {
        ans = arr[i][j];
      }
    }
    ansArr.push(ans);
  }
  return ansArr;
}

