function mutation(arr) {
  let upperCaseArr = arr.map(word => word.toUpperCase());
  let firstString = upperCaseArr[0].split("");
  let secondString = upperCaseArr[1].split("");

  let booleanArr = secondString.map(
    letter => firstString.includes(letter)
  );

  return booleanArr.includes(false) ? false : true;
}

mutation(["hello", "hey"]);
