function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  let sortedArr = arr.sort((a,b) => (a < b) ? -1 : 1);
  let element = sortedArr.find(item => (item >= num) ? true : false);

  // if the new number is greater than any number in the array
  if (!element) {
    element = arr.length;
    return element;
  }
  let index = sortedArr.indexOf(element);
  return (index >= 0) ? index : 0;
}

